mapper.update:
UPDATE comment
SET content = #{content},
    rating = #{rating}
WHERE id = #{id}

mapper.select:
SELECT * FROM comment
WHERE productId = #{productId}
  AND vendorId = #{vendorId}
ORDER BY rating DESC, createdTime

SELECT * FROM comment
WHERE productId = #{productId}
AND vendorId = #{vendorId}
AND customerId = #{customerId}
AND orderId = #{orderId}
