mapper.select:
SELECT * FROM orders
WHERE customerId = #{customerId}
ORDER BY orderDate DESC

mapper.update:
UPDATE orders
SET order_status = #{orderStatus}

    , deliveryDate = #{deliveryDate}

WHERE id = #{id}

mapper.select:
SELECT * FROM orders


        AND order_status = #{orderStatus}


        AND transporterId = #{transporterId}


ORDER BY orderDate DESC
