mapper.update:
UPDATE users
SET
    fullname = #{fullName},
    email = #{email},
    update_time = #{updateTime}
WHERE username = #{userName}

mapper.select:
SELECT count(*)
FROM users u
INNER JOIN vendor_info vi on u.id = vi.vendorId


        AND username = #{userName}


        AND email = #{email}


        AND business_code = #{businessCode}

SELECT * FROM vendor_info vi
INNER JOIN users u on vi.vendorId = u.id
WHERE vendorId = #{vendorId}

SELECT * FROM transporters t
INNER JOIN users u on t.transporterId = u.id
WHERE transporterId = #{transporterId}
